import React, { Component } from 'react';





class Counter extends React.Component {

    state= {
        count:0,
        imageUrl:'http://picsum.photos/200'
    };

   styles={
       fontSize: '10px',
       fontWeight:'bold'

   }


    render() { 
        return (
        <div>
            <img src={this.state.imageUrl} alt="" />
            {/* we can print any value here */}

            {/* We can edit the sheet of html in className: "badge badge-primary is to give a blue background and "m-2" is to set the margin to 2*/}
            <span style={this.style} className="badge badge-primary m-2">{this.formatCount()}</span> 
            <button className="btn btn-secondary btn-sm">Increment</button>
        </div> );
    }

    formatCount(){
        const { count }= this.state;
        //  Is count equals to 0, if pageYOffset, return 'Zero'
        return count ===0? 'Zero': count;
    }
}




export default Counter;
